def body_to_row(body=''):
    if '?' in body:
        body = body.split('?')[-1]
        # print(body)
    body_split = body.split('&')
    result = ""
    for r in body_split:
        result += r + '\n'

    print(result)
    return result


def headers_to_dict(headers=''):
    if headers == '':
        return ''
    items = headers.split("\n")
    # print(items)
    d = {}
    for item in items:
        k, v = item.split(": ", 1)

        d[k] = v.strip()
    print(d)
    return d


def main():
    # body_to_row(body)
    headers_to_dict(headers=headers)


if __name__ == '__main__':
    main()

